
    // ????
$(function () {
  var $demo1 = $(".items");

  function initializeMasonry() {
    $demo1.imagesLoaded(function () {
      $demo1.masonry({
        itemSelector: ".items .item",
        fitWidth: true,
      });
    });
  }

  // ウィンドウサイズが450px以下の場合の設定
  function applyMobileSettings() {
    $demo1.masonry("destroy"); // 既存のMasonryインスタンスを破棄
    $demo1.css("width", "100%"); // 幅を100%に設定
    $demo1.masonry({
      itemSelector: ".items .item",
      fitWidth: false,
    });
  }


  // ウィンドウサイズが450px以下の場合の設定
  function applyDesktopSettings() {
    $demo1.masonry("destroy"); // 既存のMasonryインスタンスを破棄
    $demo1.css("width", "100%"); // 幅を100%に設定
    $demo1.masonry({
      itemSelector: ".items .item",
      fitWidth: false,
    });
  }

  // 初期設定
  initializeMasonry();

  // ウィンドウサイズの変更を監視して設定を適用
  $(window).on("resize", function () {
    var windowWidth = $(window).width();

    if (windowWidth <= 450) {
      applyMobileSettings();
    } else if (windowWidth >= 1200) {
      applyDesktopSettings();
    }
  });
});


